import axios from "axios";
export async function httpGet(url, userAgent) {
    try {
        const res = await axios.get(url, {
            timeout: 12000,
            headers: { "User-Agent": userAgent, Accept: "text/html,application/xhtml+xml" },
            validateStatus: () => true
        });
        const headers = {};
        Object.entries(res.headers || {}).forEach(([k, v]) => (headers[k.toLowerCase()] = String(v)));
        return { ok: res.status >= 200 && res.status < 400, status: res.status, data: res.data, headers };
    }
    catch (e) {
        return { ok: false, status: 0, headers: {} };
    }
}
